PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX dc: <http://purl.org/dc/terms/>
PREFIX time: <http://www.w3.org/2006/time#>
PREFIX schema: <http://schema.org/>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>

CONSTRUCT {
  # Catalog
  <http://datos.gob.es/catalog/{org_dir3}> a dcat:Catalog ;
    dct:title "Catálogo de datos - {org_dir3}" ;
    dct:description "Catálogo de datasets del organismo {org_dir3}" ;
    dct:publisher ?publisher ;
    dcat:dataset ?dataset ;
    dct:language "es" ;
    foaf:homepage <http://datos.gob.es> .

  # Datasets - Basic properties that are most likely to exist
  ?dataset a dcat:Dataset ;
    dct:title ?title ;
    dct:description ?description ;
    dct:publisher ?publisher ;
    dcat:theme ?theme ;
    dcat:keyword ?keyword ;
    dct:identifier ?identifier ;
    dct:issued ?issued ;
    dct:modified ?modified ;
    dct:language ?language ;
    dct:license ?license ;
    dcat:distribution ?distribution .

  # Distributions - Basic properties
  ?distribution a dcat:Distribution ;
    dct:title ?distTitle ;
    dct:description ?distDescription ;
    dcat:downloadURL ?downloadURL ;
    dcat:accessURL ?accessURL ;
    dcat:mediaType ?mediaType ;
    dct:format ?format ;
    dct:license ?distLicense .
}
WHERE {
  # Find datasets for the organization - Try different patterns
  {
    # Pattern 1: Direct publisher match
    ?dataset a dcat:Dataset .
    ?dataset dct:publisher ?publisher .
    FILTER (regex(str(?publisher), "{org_dir3}", "i"))
  }
  UNION
  {
    # Pattern 2: Catalog -> Dataset relationship
    ?catalog a dcat:Catalog .
    ?catalog dcat:dataset ?dataset .
    ?catalog dct:publisher ?publisher .
    FILTER (regex(str(?publisher), "{org_dir3}", "i"))
  }

  # Dataset properties - Only include commonly available properties
  OPTIONAL { ?dataset dct:title ?title }
  OPTIONAL { ?dataset dct:description ?description }
  OPTIONAL { ?dataset dcat:theme ?theme }
  OPTIONAL { ?dataset dcat:keyword ?keyword }
  OPTIONAL { ?dataset dct:identifier ?identifier }
  OPTIONAL { ?dataset dct:issued ?issued }
  OPTIONAL { ?dataset dct:modified ?modified }
  OPTIONAL { ?dataset dct:language ?language }
  OPTIONAL { ?dataset dct:license ?license }

  # Distributions - Only basic properties
  OPTIONAL {
    ?dataset dcat:distribution ?distribution .
    OPTIONAL { ?distribution dct:title ?distTitle }
    OPTIONAL { ?distribution dct:description ?distDescription }
    OPTIONAL { ?distribution dcat:downloadURL ?downloadURL }
    OPTIONAL { ?distribution dcat:accessURL ?accessURL }
    OPTIONAL { ?distribution dcat:mediaType ?mediaType }
    OPTIONAL { ?distribution dct:format ?format }
    OPTIONAL { ?distribution dct:license ?distLicense }
  }
}
LIMIT 50

