# Test query to verify endpoint connectivity and basic data retrieval
PREFIX dcat: <http://www.w3.org/ns/dcat#>
PREFIX dct: <http://purl.org/dc/terms/>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>

SELECT DISTINCT ?type (COUNT(*) as ?count) WHERE {
  {
    ?s a ?type .
  }
  UNION
  {
    ?s dct:type ?type .
  }
  UNION 
  {
    ?s a dcat:Dataset .
    BIND(dcat:Dataset as ?type)
  }
  UNION
  {
    ?s a dcat:Catalog .
    BIND(dcat:Catalog as ?type)
  }
  UNION
  {
    ?s a dcat:Distribution .
    BIND(dcat:Distribution as ?type)
  }
}
GROUP BY ?type
ORDER BY DESC(?count)
LIMIT 20

